import React from 'react';
import { motion } from 'framer-motion';
import { Clock, Zap, CreditCard } from 'lucide-react';

const CustomerDashboard = () => {
  return (
    <div className="min-h-screen bg-gray-50 p-6 md:p-12">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-black uppercase italic mb-8">Welcome, <span className="text-gymRed">Athlete</span></h1>

        <div className="grid md:grid-cols-2 gap-6 mb-10">
          {/* Plan Status Card */}
          <div className="bg-white p-8 rounded-3xl border-2 border-black shadow-sm">
            <h3 className="text-xs font-black uppercase tracking-widest text-gray-400 mb-4">Current Plan</h3>
            <p className="text-2xl font-black italic uppercase">3 Months + Extra Time</p>
            <div className="mt-6 flex items-center gap-2 text-green-600 font-bold">
              <div className="w-2 h-2 bg-green-600 rounded-full animate-pulse"></div>
              ACTIVE UNTIL: OCT 2025
            </div>
          </div>

          {/* Machine Usage Card */}
          <div className="bg-black text-white p-8 rounded-3xl shadow-xl">
            <h3 className="text-xs font-black uppercase tracking-widest text-gymYellow mb-4">Machine Access</h3>
            <div className="flex justify-between items-end">
              <div>
                <p className="text-4xl font-black italic">50 MINS</p>
                <p className="text-[10px] uppercase font-bold text-gray-400 mt-1">Daily Limit (Treadmill & Vibration)</p>
              </div>
              <Zap className="text-gymYellow" size={40} />
            </div>
          </div>
        </div>

        {/* Payment Action for Pending Users */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-gymYellow/10 border-2 border-dashed border-gymYellow p-8 rounded-3xl text-center"
        >
          <CreditCard className="mx-auto mb-4" size={32} />
          <h3 className="text-xl font-black uppercase">Next Renewal Pending</h3>
          <p className="text-sm font-medium text-gray-600 mt-2 mb-6">Your registration fee (â‚¹500) expires in 12 days.</p>
          <button className="bg-black text-white px-8 py-3 rounded-xl font-black uppercase text-xs tracking-widest">
            Pay Now
          </button>
        </motion.div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
