import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const role = localStorage.getItem('userRole'); // Check if someone is logged in

  const handleLogout = () => {
    localStorage.clear(); // Remove role and name
    navigate('/login');
  };

  return (
    <nav className="sticky top-0 z-50 bg-white shadow-sm border-b-4 border-gymYellow px-6 py-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <Link to="/" className="flex items-center gap-2">
          <img src="/GYM_LOGO.png" alt="Logo" className="h-12 w-12 rounded-full border-2 border-gymYellow" />
          <h1 className="text-xl font-black uppercase tracking-tighter hidden md:block">
            Gymnastics <span className="text-gymRed">Fit Planet</span>
          </h1>
        </Link>

        <div className="hidden md:flex gap-8 text-xs font-bold uppercase tracking-widest">
          <Link to="/" className="hover:text-gymRed">Home</Link>
          <Link to="/services" className="hover:text-gymRed">Services</Link>
          <Link to="/about" className="hover:text-gymRed">About Us</Link>
        </div>

        <div className="flex items-center gap-4">
          {!role ? (
            <Link to="/login" className="bg-gymRed text-white px-6 py-2 rounded font-black text-xs uppercase hover:bg-black transition">
              Login
            </Link>
          ) : (
            <button 
              onClick={handleLogout}
              className="bg-black text-white px-6 py-2 rounded font-black text-xs uppercase hover:bg-gymRed transition"
            >
              Logout
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
