import React, { useState } from 'react';

const Register = () => {
  const [extra, setExtra] = useState(false);
  const [method, setMethod] = useState('Counter');
  const regFee = 500;

  const [selectedPlan, setSelectedPlan] = useState({ name: 'Monthly', price: 1000, addOn: 500 });

  const plans = [
    { name: 'Monthly', price: 1000, addOn: 500 },
    { name: '3 Months', price: 2700, addOn: 1500 },
    { name: '6 Months', price: 5200, addOn: 3000 },
    { name: 'Yearly', price: 10000, addOn: 5000 },
  ];

  const total = regFee + selectedPlan.price + (extra ? selectedPlan.addOn : 0);

  return (
    <div className="max-w-5xl mx-auto py-12 px-6">
      <h1 className="text-4xl font-black mb-8 text-center uppercase">Member Registration</h1>
      
      <div className="grid lg:grid-cols-2 gap-12">
        {/* Step 1: Selection */}
        <div className="space-y-6">
          <div className="bg-gray-50 p-6 rounded-xl border-l-4 border-gymRed shadow-sm">
            <h3 className="font-bold text-xl mb-4 italic uppercase">1. Choose Subscription</h3>
            <div className="grid grid-cols-2 gap-4">
              {plans.map((p) => (
                <button 
                  key={p.name}
                  onClick={() => setSelectedPlan(p)}
                  className={`p-4 border-2 rounded font-bold transition ${selectedPlan.name === p.name ? 'border-gymRed bg-red-50' : 'border-gray-200 bg-white'}`}
                >
                  {p.name} <br/> ₹{p.price}
                </button>
              ))}
            </div>
            
            <label className="mt-6 flex items-center p-4 bg-gymYellow/20 rounded cursor-pointer border-2 border-gymYellow">
              <input type="checkbox" className="w-5 h-5 mr-3" onChange={() => setExtra(!extra)} />
              <span className="font-bold">Add 30 Mins Extra Treadmill/Vibration (+₹{selectedPlan.addOn})</span>
            </label>
          </div>

          <div className="bg-gray-50 p-6 rounded-xl border-l-4 border-black shadow-sm">
            <h3 className="font-bold text-xl mb-4 italic uppercase">2. Select Payment Method</h3>
            <div className="flex gap-4">
              <button onClick={() => setMethod('QR')} className={`flex-1 py-3 rounded font-bold border-2 ${method === 'QR' ? 'bg-black text-white' : 'bg-white'}`}>Online (QR)</button>
              <button onClick={() => setMethod('Counter')} className={`flex-1 py-3 rounded font-bold border-2 ${method === 'Counter' ? 'bg-black text-white' : 'bg-white'}`}>Pay at Counter</button>
            </div>
            {method === 'QR' && (
              <div className="mt-4 p-4 bg-white border-2 border-dashed border-gray-300 text-center">
                <p className="text-sm font-bold text-gray-600 mb-2">Scan QR & Upload Screenshot</p>
                <div className="w-32 h-32 bg-gray-200 mx-auto mb-4">[QR IMAGE]</div>
                <input type="file" className="text-xs" />
              </div>
            )}
          </div>
        </div>

        {/* Step 2: Summary */}
        <div className="bg-white p-8 rounded-2xl border-2 border-black card-shadow h-fit">
          <h3 className="text-2xl font-black mb-6 uppercase border-b-2 border-gray-100 pb-2">Checkout Summary</h3>
          <div className="space-y-4 text-lg">
            <div className="flex justify-between"><span>Registration Fee (6 Mo):</span><span className="font-bold">₹{regFee}</span></div>
            <div className="flex justify-between"><span>{selectedPlan.name} Plan:</span><span className="font-bold">₹{selectedPlan.price}</span></div>
            {extra && <div className="flex justify-between text-gymRed font-bold"><span>Add-on (Extra Time):</span><span>₹{selectedPlan.addOn}</span></div>}
            <div className="h-px bg-gray-200 my-4"></div>
            <div className="flex justify-between text-3xl font-black italic">
              <span>TOTAL:</span>
              <span className="text-gymRed">₹{total}</span>
            </div>
          </div>
          <button className="w-full mt-8 bg-black text-white py-4 rounded-lg font-black text-xl hover:bg-gymRed transition uppercase tracking-widest">
            Confirm & Pay
          </button>
        </div>
      </div>
    </div>
  );
};

export default Register;
