import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShieldCheck, User, Lock, Mail } from 'lucide-react';
import { motion } from 'framer-motion';

const Login = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // MOCK CREDENTIALS LOGIC
    if (isAdmin) {
      if (email === "admin@gym.com" && password === "admin123") {
        localStorage.setItem('userRole', 'admin');
        navigate('/dashboard');
      } else { alert("Invalid Admin Credentials"); }
    } else {
      if (email === "user@gym.com" && password === "user123") {
        localStorage.setItem('userRole', 'customer');
        localStorage.setItem('userName', 'Rahul Singh'); // Mock name
        navigate('/dashboard');
      } else { alert("Invalid Member Credentials"); }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-6">
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }}
        className="max-w-md w-full bg-white border-2 border-black rounded-3xl shadow-[15px_15px_0px_0px_rgba(0,0,0,1)] overflow-hidden"
      >
        {/* Toggle Header */}
        <div className="flex border-b-2 border-black font-black uppercase text-xs">
          <button onClick={() => setIsAdmin(false)} className={`flex-1 py-4 transition ${!isAdmin ? 'bg-gymRed text-white' : 'bg-white'}`}>Member Login</button>
          <button onClick={() => setIsAdmin(true)} className={`flex-1 py-4 transition ${isAdmin ? 'bg-black text-white' : 'bg-white'}`}>Admin Portal</button>
        </div>

        <form onSubmit={handleLogin} className="p-10 space-y-6">
          <div className="text-center mb-6">
            {isAdmin ? <ShieldCheck className="mx-auto mb-2 text-black" size={40}/> : <User className="mx-auto mb-2 text-gymRed" size={40}/>}
            <h2 className="text-2xl font-black uppercase italic">{isAdmin ? "Staff Access" : "Athlete Login"}</h2>
          </div>

          <div className="space-y-4">
            <div className="relative">
              <Mail className="absolute left-3 top-3.5 text-gray-400" size={18}/>
              <input type="email" placeholder="Email Address" required className="w-full pl-10 pr-4 py-3 border-2 border-gray-100 rounded-xl outline-none focus:border-black font-bold" onChange={(e)=>setEmail(e.target.value)} />
            </div>
            <div className="relative">
              <Lock className="absolute left-3 top-3.5 text-gray-400" size={18}/>
              <input type="password" placeholder="Password" required className="w-full pl-10 pr-4 py-3 border-2 border-gray-100 rounded-xl outline-none focus:border-black font-bold" onChange={(e)=>setPassword(e.target.value)} />
            </div>
          </div>

          <button type="submit" className={`w-full py-4 rounded-xl font-black uppercase tracking-widest text-white transition ${isAdmin ? 'bg-black hover:bg-gray-800' : 'bg-gymRed hover:bg-red-700'}`}>
            Sign In
          </button>
        </form>
      </motion.div>
    </div>
  );
};

export default Login;
