import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShieldCheck, User } from 'lucide-react';

const Login = () => {
  const navigate = useNavigate();

  const handleMockLogin = (role) => {
    // We store the role in localStorage to "remember" who logged in without a backend
    localStorage.setItem('userRole', role);
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen bg-white flex items-center justify-center px-6">
      <div className="max-w-4xl w-full grid md:grid-cols-2 gap-8">
        
        {/* Admin Login Section */}
        <div 
          onClick={() => handleMockLogin('admin')}
          className="group border-4 border-black p-10 rounded-3xl cursor-pointer hover:bg-black hover:text-white transition-all duration-500 shadow-[10px_10px_0px_0px_rgba(0,0,0,1)]"
        >
          <ShieldCheck size={48} className="text-gymRed mb-6" />
          <h2 className="text-3xl font-black uppercase italic mb-4">Admin Portal</h2>
          <p className="text-sm font-medium opacity-60 uppercase tracking-widest">Management & Staff Only</p>
        </div>

        {/* Customer Login Section */}
        <div 
          onClick={() => handleMockLogin('customer')}
          className="group border-4 border-gymRed p-10 rounded-3xl cursor-pointer hover:bg-gymRed hover:text-white transition-all duration-500 shadow-[10px_10px_0px_0px_rgba(227,30,36,1)]"
        >
          <User size={48} className="text-black group-hover:text-white mb-6" />
          <h2 className="text-3xl font-black uppercase italic mb-4">Member Login</h2>
          <p className="text-sm font-medium opacity-60 uppercase tracking-widest">Access your fitness journey</p>
        </div>

      </div>
    </div>
  );
};

export default Login;
